import React from 'react'
import Navbar from './components/navbar'
import NewTodo from './components/newTodo'
import TodoList from './components/todoList'
import './styles/app.css'

class App extends React.Component {
  state = {
    todos: [],
    countText: '0 items left'
  }

  handleCount() {
    // const count = this.state.todos.filter(todo => !todo.completed).length
    const count = this.state.todos.length
    const plural = count === 1 ? 'item' : 'items'
    const countText = `${count} ${plural} left`
    console.log(countText)
    this.setState({ countText })
  }

  handleDelete = todoId => {
    let todos = this.state.todos
    todos.splice(todoId, 1)
    this.setState({ todos })
    this.handleCount()
  }

  handleCompleted = todoId => {
    let todos = this.state.todos
    todos[todoId].completed = !todos[todoId].completed
    this.setState({ todos })
    this.handleCount()
  }

  handleNewTodo = todo => {
    const todos = [...this.state.todos, todo]
    this.setState({ todos })
    console.log(todos)
    this.handleCount()
  }

  render() {
    return (
      <React.Fragment>
        <Navbar countText={this.state.countText} />
        <NewTodo onNewTodo={this.handleNewTodo} />
        <TodoList
          todoList={this.state.todos}
          onCompleted={this.handleCompleted}
          onDelete={this.handleDelete}
        />
      </React.Fragment>
    )
  }
}
export default App
